                        <?php 
                             if (isset($data['cart'])){
                                $decode = json_decode($data['cart'], true);
                                $totalOrder = 0;
                                $totalFreight = 0;
                                foreach($decode as $value)
                                {  
                        ?>
                        <div class="order-products__row">
                            <div class="order-products__row-block block1">
                                <img class="order-products__row-product-img" src="<?php echo $value['imageUrl']?> " alt="">
                                <h4 class="order-products__row-product-name">
                                    <?php echo $value['productName']?> 
                                </h4>
                                <p class="order-products__row-product-type order-products__sub-title">
                                    <?php echo $value['productTypeName']?> 
                                </p>
                            </div>
                            <div class="order-products__row-block block2">
                                <div class="order-products__row-product-unit-price">
                                    <?php $price = $value['productTypePrice'] * (1 - $value['productDiscount']); echo viPrice($price, 'đ');?> 
                                </div>
                            </div>
                            <div class="order-products__row-block block3">
                                <div class="order-products__row-product-quantity">
                                    <?php echo $value['cartQuantity']?> 
                                </div>
                            </div>
                            <div class="order-products__row-block block4">
                                <div class="order-products__row-product-price">
                                    <?php 
                                        $total = $value['cartQuantity'] * $price;
                                        $totalOrder += $total;
                                        $totalFreight += $value['productFreightCost'];
                                        echo viPrice($total, 'đ');
                                    ?>
                                </div>
                            </div>
                        </div>
                    <?php } }?>
